mapper.update:
update setmeal
status = #{status}, category_id = #{categoryId}, description = #{description}, image = #{image}, name = #{name}, price = #{price}
id = #{id}

mapper.select:
select s.*, c.name AS category_name from setmeal s LEFT OUTER JOIN category c ON s.category_id = c.id 
 s.category_id = #{categoryId}
s.name = #{name}
s.status = #{status}
